export const handleAddWord = (word) => {
    return dispatch => {
        return addNewWord(word)
            .then(word => dispatch(addWord(word)))
    }
}
export const handleInitialData = () => {
    return dispatch => {
        return getAllWords()
            .then(words => dispatch(receiveWords(words)))
    }
}
